import React, { useState } from "react";
import PlanSummary from "./PlanSummary";
import IncludedAccounts from "./IncludedAccounts";
import IncludedLoans from "./IncludedLoans";
import IncludedCreditCards from "./IncludedCreditCards";
import IncludedInvestmentAccounts from "./IncludedInvestmentAccounts";

const tabs = [
  { label: "Plans", key: "plans" },
  { label: "Accounts", key: "accounts" },
  { label: "Loans", key: "loans" },
  { label: "Credit Cards", key: "creditCards" },
  { label: "Investment Accounts", key: "investmentAccounts" },
];

const Dashboard = () => {
  const [activeTab, setActiveTab] = useState("plans");

  return (
    <div className="min-h-screen bg-purple-50 font-sans">
      <nav className="flex border-b-2 border-purple-300 bg-white">
        {tabs.map((tab) => (
          <button
            key={tab.key}
            onClick={() => setActiveTab(tab.key)}
            className={`flex-1 py-4 text-lg font-semibold transition-colors duration-200 border-b-4 focus:outline-none 
              ${activeTab === tab.key
                ? "bg-purple-600 text-white border-purple-300"
                : "bg-transparent text-purple-900 border-transparent hover:bg-purple-100"}
            `}
          >
            {tab.label}
          </button>
        ))}
      </nav>
      <main className="max-w-3xl mx-auto mt-8 p-4">
        {activeTab === "plans" && (
          <>
            <PlanSummary />
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-8">
              <IncludedAccounts />
              <IncludedLoans />
              <IncludedCreditCards />
              <IncludedInvestmentAccounts />
            </div>
          </>
        )}
        {activeTab === "accounts" && <IncludedAccounts fullPage />}
        {activeTab === "loans" && <IncludedLoans fullPage />}
        {activeTab === "creditCards" && <IncludedCreditCards fullPage />}
        {activeTab === "investmentAccounts" && <IncludedInvestmentAccounts fullPage />}
      </main>
    </div>
  );
};

export default Dashboard;
