import React, { useState } from "react";
import PlanSummary from "./PlanSummary";
import IncludedAccounts from "./IncludedAccounts";
import IncludedLoans from "./IncludedLoans";
import IncludedCreditCards from "./IncludedCreditCards";
import IncludedInvestmentAccounts from "./IncludedInvestmentAccounts";

const tabs = [
  { label: "Plans", key: "plans" },
  { label: "Accounts", key: "accounts" },
  { label: "Loans", key: "loans" },
  { label: "Credit Cards", key: "creditCards" },
  { label: "Investment Accounts", key: "investmentAccounts" },
];

const purple = {
  background: "#f5f3ff",
  primary: "#7c3aed",
  secondary: "#a78bfa",
  text: "#3b0764",
  white: "#fff",
};

const Dashboard = () => {
  const [activeTab, setActiveTab] = useState("plans");

  return (
    <div
      style={{
        minHeight: "100vh",
        background: purple.background,
        fontFamily: "Inter, sans-serif",
      }}
    >
      <nav
        style={{
          display: "flex",
          borderBottom: `2px solid ${purple.secondary}`,
          background: purple.white,
        }}
      >
        {tabs.map((tab) => (
          <button
            key={tab.key}
            onClick={() => setActiveTab(tab.key)}
            style={{
              flex: 1,
              padding: "1rem",
              background:
                activeTab === tab.key ? purple.primary : "transparent",
              color: activeTab === tab.key ? purple.white : purple.text,
              border: "none",
              borderBottom:
                activeTab === tab.key
                  ? `4px solid ${purple.secondary}`
                  : "none",
              fontWeight: 600,
              fontSize: "1.1rem",
              cursor: "pointer",
              transition: "background 0.2s",
            }}
          >
            {tab.label}
          </button>
        ))}
      </nav>
      <main style={{ maxWidth: 900, margin: "2rem auto", padding: "1rem" }}>
        {activeTab === "plans" && (
          <>
            <PlanSummary />
            <div
              style={{
                display: "grid",
                gridTemplateColumns: "1fr 1fr",
                gap: "1.5rem",
                marginTop: "2rem",
              }}
            >
              <IncludedAccounts />
              <IncludedLoans />
              <IncludedCreditCards />
              <IncludedInvestmentAccounts />
            </div>
          </>
        )}
        {activeTab === "accounts" && <IncludedAccounts fullPage />}
        {activeTab === "loans" && <IncludedLoans fullPage />}
        {activeTab === "creditCards" && <IncludedCreditCards fullPage />}
        {activeTab === "investmentAccounts" && (
          <IncludedInvestmentAccounts fullPage />
        )}
      </main>
    </div>
  );
};

export default Dashboard;
