import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";

const NewPlanForm = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: "",
    startDate: "",
    endDate: "",
    projectedValue: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // In a real application, you would submit this data to your backend
    console.log("Form submitted:", formData);
    alert("New plan created (just a placeholder, not actually saved)");
    // Navigate back to the plans dashboard
    navigate("/");
  };

  return (
    <div className="max-w-2xl mx-auto mt-8 p-4">
      <Link to="/" className="text-purple-600 hover:text-purple-800 mb-6 inline-block">
        &larr; Back to All Plans
      </Link>

      <div className="bg-purple-50 rounded-xl p-8 shadow-md">
        <h2 className="text-2xl text-purple-700 font-bold mb-6">Create New Financial Plan</h2>

        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label htmlFor="name" className="block text-purple-700 font-semibold mb-2">
              Plan Name
            </label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              className="w-full p-2 border border-purple-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
              required
            />
          </div>

          <div className="mb-4">
            <label htmlFor="startDate" className="block text-purple-700 font-semibold mb-2">
              Start Date
            </label>
            <input
              type="date"
              id="startDate"
              name="startDate"
              value={formData.startDate}
              onChange={handleChange}
              className="w-full p-2 border border-purple-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
              required
            />
          </div>

          <div className="mb-4">
            <label htmlFor="endDate" className="block text-purple-700 font-semibold mb-2">
              End Date
            </label>
            <input
              type="date"
              id="endDate"
              name="endDate"
              value={formData.endDate}
              onChange={handleChange}
              className="w-full p-2 border border-purple-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
              required
            />
          </div>

          <div className="mb-6">
            <label htmlFor="projectedValue" className="block text-purple-700 font-semibold mb-2">
              Projected Value ($)
            </label>
            <input
              type="number"
              id="projectedValue"
              name="projectedValue"
              value={formData.projectedValue}
              onChange={handleChange}
              className="w-full p-2 border border-purple-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
              min="0"
              required
            />
          </div>

          <div className="flex justify-end space-x-4">
            <Link 
              to="/"
              className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-semibold py-2 px-4 rounded-lg transition duration-200"
            >
              Cancel
            </Link>
            <button
              type="submit"
              className="bg-purple-600 hover:bg-purple-700 text-white font-semibold py-2 px-4 rounded-lg transition duration-200"
            >
              Create Plan
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default NewPlanForm;
