import React from "react";

const IncludedAccounts = ({ fullPage }) => {
  // Dummy data
  const accounts = [
    { name: "Checking Account", balance: 3500 },
    { name: "Savings Account", balance: 12000 },
  ];

  return (
    <section
      style={{
        background: "#f3e8ff",
        borderRadius: 12,
        padding: "1.2rem",
        minHeight: 120,
        boxShadow: "0 1px 4px #a78bfa22",
        gridColumn: fullPage ? "1 / -1" : undefined,
      }}
    >
      <h3 style={{ color: "#7c3aed", marginBottom: 8 }}>Included Accounts</h3>
      <ul style={{ listStyle: "none", padding: 0, margin: 0 }}>
        {accounts.map((acc, i) => (
          <li key={i} style={{ marginBottom: 6, color: "#3b0764" }}>
            {acc.name}:{" "}
            <span style={{ color: "#7c3aed", fontWeight: 600 }}>
              ${acc.balance.toLocaleString()}
            </span>
          </li>
        ))}
      </ul>
    </section>
  );
};

export default IncludedAccounts;
