import React from "react";
import {
  BrowserRouter,
  Routes,
  Route,
  Navigate,
  NavLink,
} from "react-router-dom";
import Dashboard from "./components/Dashboard";
import PlansDashboard from "./components/PlansDashboard";
import PlanSummary from "./components/PlanSummary";
import NewPlanForm from "./components/NewPlanForm";
import TailwindTest from "./components/TailwindTest";
import ErrorBoundary from "./components/ErrorBoundary";

const tabs = [
  { label: "Plans", key: "plans", path: "/dashboard/plans" },
  { label: "Accounts", key: "accounts", path: "/dashboard/accounts" },
  { label: "Loans", key: "loans", path: "/dashboard/loans" },
  {
    label: "Credit Cards",
    key: "creditCards",
    path: "/dashboard/credit-cards",
  },
  {
    label: "Investment Accounts",
    key: "investmentAccounts",
    path: "/dashboard/investments",
  },
];

function App() {
  return (
    <ErrorBoundary>
      <BrowserRouter>
        <div className="min-h-screen bg-purple-50 font-sans">
          {/* App Header */}
          <header className="bg-white shadow-sm">
            <div className="container mx-auto px-4 py-3 flex items-center">
              <div className="text-2xl font-bold text-purple-700 mr-10">
                Finance Tracker
              </div>
            </div>
          </header>

          {/* Persistent Navigation */}
          <nav className="flex border-b-2 border-purple-300 bg-white">
            {tabs.map((tab) => (
              <NavLink
                key={tab.key}
                to={tab.path}
                className={({ isActive }) =>
                  `flex-1 py-4 text-center text-lg font-semibold transition-colors duration-200 border-b-4 focus:outline-none
                ${
                  isActive
                    ? "bg-purple-600 text-white border-purple-300"
                    : "bg-transparent text-purple-900 border-transparent hover:bg-purple-100"
                }`
                }
              >
                {tab.label}
              </NavLink>
            ))}
          </nav>

          <div className="container mx-auto px-4 py-6">
            <Routes>
              {/* Redirect root to dashboard/plans */}
              <Route
                path="/"
                element={<Navigate to="/dashboard/plans" replace />}
              />

              {/* Dashboard routes */}
              <Route path="/dashboard/plans" element={<PlansDashboard />} />
              <Route
                path="/dashboard/accounts"
                element={<Dashboard section="accounts" />}
              />
              <Route
                path="/dashboard/loans"
                element={<Dashboard section="loans" />}
              />
              <Route
                path="/dashboard/credit-cards"
                element={<Dashboard section="creditCards" />}
              />
              <Route
                path="/dashboard/investments"
                element={<Dashboard section="investmentAccounts" />}
              />

              {/* Plan-specific routes */}
              <Route path="/plans/new" element={<NewPlanForm />} />
              <Route path="/plans/:planId" element={<PlanSummary />} />

              {/* Legacy routes */}
              <Route
                path="/dashboard"
                element={<Navigate to="/dashboard/plans" replace />}
              />
              <Route path="/tailwind-test" element={<TailwindTest />} />

              {/* Catch all */}
              <Route
                path="*"
                element={<Navigate to="/dashboard/plans" replace />}
              />
            </Routes>
          </div>
        </div>
      </BrowserRouter>
    </ErrorBoundary>
  );
}

export default App;
