import React from "react";
import { Link } from "react-router-dom";

const PlansDashboard = () => {
  // Dummy data for available plans
  const plans = [
    { id: 1, name: "Retirement Plan", startDate: "2025-01-01", endDate: "2055-01-01", projectedValue: 1250000 },
    { id: 2, name: "College Fund", startDate: "2025-06-01", endDate: "2043-08-01", projectedValue: 250000 },
    { id: 3, name: "Home Purchase", startDate: "2025-03-15", endDate: "2030-03-15", projectedValue: 180000 }
  ];

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-purple-700">Your Financial Plans</h1>
        <Link 
          to="/plans/new" 
          className="bg-purple-600 hover:bg-purple-700 text-white font-semibold py-2 px-4 rounded-lg transition duration-200"
        >
          Add New Plan
        </Link>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {plans.map((plan) => (
          <Link 
            key={plan.id} 
            to={`/plans/${plan.id}`} 
            className="block bg-purple-50 hover:bg-purple-100 rounded-xl p-6 shadow-md transition duration-200"
          >
            <h2 className="text-xl font-bold text-purple-700 mb-2">{plan.name}</h2>
            <div className="text-purple-900">
              <p className="mb-1">Start: {new Date(plan.startDate).toLocaleDateString()}</p>
              <p className="mb-1">End: {new Date(plan.endDate).toLocaleDateString()}</p>
              <p className="font-semibold mt-3">
                Projected Value: ${plan.projectedValue.toLocaleString()}
              </p>
            </div>
          </Link>
        ))}
      </div>

      {plans.length === 0 && (
        <div className="text-center py-12 bg-purple-50 rounded-xl">
          <p className="text-xl text-purple-700">You don't have any financial plans yet.</p>
          <p className="text-gray-600 mt-2">Create your first plan to get started!</p>
        </div>
      )}
    </div>
  );
};

export default PlansDashboard;
