import React from "react";

const IncludedLoans = ({ fullPage }) => {
  // Dummy data
  const loans = [
    { name: "Car Loan", balance: 8000 },
    { name: "Student Loan", balance: 22000 },
  ];

  return (
    <section
      style={{
        background: "#f3e8ff",
        borderRadius: 12,
        padding: "1.2rem",
        minHeight: 120,
        boxShadow: "0 1px 4px #a78bfa22",
        gridColumn: fullPage ? "1 / -1" : undefined,
      }}
    >
      <h3 style={{ color: "#7c3aed", marginBottom: 8 }}>Included Loans</h3>
      <ul style={{ listStyle: "none", padding: 0, margin: 0 }}>
        {loans.map((loan, i) => (
          <li key={i} style={{ marginBottom: 6, color: "#3b0764" }}>
            {loan.name}:{" "}
            <span style={{ color: "#7c3aed", fontWeight: 600 }}>
              ${loan.balance.toLocaleString()}
            </span>
          </li>
        ))}
      </ul>
    </section>
  );
};

export default IncludedLoans;
