import React, { useState, useEffect, useContext } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { usePlans } from '../context/PlanContext';
import { AccountContext } from '../context/AccountContext';
import AccountList from './AccountList';
import AccountModal from './AccountModal';

const PlanDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { getPlan, linkAccountToPlan, unlinkAccountFromPlan, loading, error } = usePlans();
  const [plan, setPlan] = useState(null);
  const [activeTab, setActiveTab] = useState('overview');
  const [refreshTrigger, setRefreshTrigger] = useState(0);

  // Fetch plan data
  useEffect(() => {
    const fetchPlanData = async () => {
      const planData = await getPlan(id);
      if (planData) {
        setPlan(planData);
      }
    };

    fetchPlanData();
  }, [id, getPlan, refreshTrigger]);

  // Modal state for adding accounts
  const [showModal, setShowModal] = useState(false);
  const [modalType, setModalType] = useState(null);

  // Open modal with specified type
  const openModal = (type) => {
    setModalType(type);
    setShowModal(true);
  };

  // Close modal
  const closeModal = () => {
    setShowModal(false);
    setModalType(null);
  };

  // Format dates
  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  // Format currency
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
    }).format(amount);
  };

  // Handle unlinking an account
  const handleUnlinkAccount = async (accountId, accountType) => {
    if (window.confirm('Are you sure you want to unlink this account from the plan?')) {
      try {
        await unlinkAccountFromPlan(id, accountId, accountType);
        // Refresh plan data
        setRefreshTrigger(prev => prev + 1);
      } catch (err) {
        console.error('Error unlinking account:', err);
      }
    }
  };

  // Refresh plan data after linking an account
  const handleAccountLinked = () => {
    setRefreshTrigger(prev => prev + 1);
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-xl text-purple-600">Loading plan details...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 p-8 rounded-xl text-center max-w-3xl mx-auto mt-8">
        <h2 className="text-2xl text-red-600 mb-4">Error</h2>
        <p className="mb-6">{error}</p>
        <Link
          to="/dashboard/plans"
          className="bg-purple-600 hover:bg-purple-700 text-white py-2 px-4 rounded-lg"
        >
          Return to Dashboard
        </Link>
      </div>
    );
  }

  if (!plan) {
    return (
      <div className="bg-red-50 p-8 rounded-xl text-center max-w-3xl mx-auto mt-8">
        <h2 className="text-2xl text-red-600 mb-4">Plan Not Found</h2>
        <p className="mb-6">
          The plan you're looking for doesn't exist or has been removed.
        </p>
        <Link
          to="/dashboard/plans"
          className="bg-purple-600 hover:bg-purple-700 text-white py-2 px-4 rounded-lg"
        >
          Return to Dashboard
        </Link>
      </div>
    );
  }

  return (
    <div className="max-w-5xl mx-auto mt-8 p-4">
      <Link
        to="/dashboard/plans"
        className="text-purple-600 hover:text-purple-800 mb-6 inline-block"
      >
        &larr; Back to All Plans
      </Link>

      {/* Plan Header */}
      <section className="bg-purple-100 rounded-2xl p-8 mb-8 shadow-md">
        <div className="flex justify-between items-start mb-6">
          <h2 className="text-purple-600 text-3xl font-bold">{plan.name}</h2>
          <div className="space-x-2">
            <Link
              to={`/plans/${id}/edit`}
              className="bg-purple-600 hover:bg-purple-700 text-white py-2 px-4 rounded-lg inline-block"
            >
              Edit Plan
            </Link>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
          <div className="bg-white rounded-lg p-4 shadow-sm">
            <h3 className="text-purple-600 font-semibold mb-1">Start Date</h3>
            <p className="text-lg">{formatDate(plan.start_date)}</p>
          </div>

          <div className="bg-white rounded-lg p-4 shadow-sm">
            <h3 className="text-purple-600 font-semibold mb-1">End Date</h3>
            <p className="text-lg">{formatDate(plan.end_date)}</p>
          </div>

          <div className="bg-white rounded-lg p-4 shadow-sm">
            <h3 className="text-purple-600 font-semibold mb-1">Target Amount</h3>
            <p className="text-xl text-purple-700 font-bold">
              ${plan.target_amount ? plan.target_amount.toLocaleString() : '0'}
            </p>
          </div>
        </div>

        {plan.description && (
          <div className="bg-white rounded-lg p-4 shadow-sm">
            <h3 className="text-purple-600 font-semibold mb-1">Description</h3>
            <p className="text-gray-700">{plan.description}</p>
          </div>
        )}
      </section>

      {/* Tabs for different sections */}
      <div className="mb-6 border-b border-gray-200">
        <ul className="flex flex-wrap -mb-px">
          <li className="mr-2">
            <button
              onClick={() => setActiveTab('overview')}
              className={`inline-block p-4 border-b-2 rounded-t-lg ${
                activeTab === 'overview'
                  ? 'text-purple-600 border-purple-600'
                  : 'border-transparent hover:text-gray-600 hover:border-gray-300'
              }`}
            >
              Overview
            </button>
          </li>
          <li className="mr-2">
            <button
              onClick={() => setActiveTab('accounts')}
              className={`inline-block p-4 border-b-2 rounded-t-lg ${
                activeTab === 'accounts'
                  ? 'text-purple-600 border-purple-600'
                  : 'border-transparent hover:text-gray-600 hover:border-gray-300'
              }`}
            >
              Accounts
            </button>
          </li>
        </ul>
      </div>

      {/* Account Cards Section */}
      {activeTab === 'accounts' && (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {/* Standard Accounts Card */}
          <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-200">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-semibold text-purple-700">Standard Accounts</h3>
              <button
                onClick={() => openModal('standard')}
                className="text-purple-600 hover:text-purple-800 font-medium flex items-center"
              >
                <svg className="w-5 h-5 mr-1" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
                  <path fillRule="evenodd" d="M10 5a1 1 0 011 1v3h3a1 1 0 110 2h-3v3a1 1 0 11-2 0v-3H6a1 1 0 110-2h3V6a1 1 0 011-1z" clipRule="evenodd" />
                </svg>
                Add Account
              </button>
            </div>

            {plan.accounts && plan.accounts.length > 0 ? (
              <ul className="divide-y divide-gray-200">
                {plan.accounts.map(account => (
                  <li key={account.id} className="py-3 flex justify-between">
                    <span>{account.name}</span>
                    <span className="font-medium">${account.balance.toLocaleString()}</span>
                  </li>
                ))}
              </ul>
            ) : (
              <p className="text-gray-500 text-center py-4">No standard accounts added</p>
            )}
          </div>

          {/* Credit Accounts Card */}
          <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-200">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-semibold text-purple-700">Credit Accounts</h3>
              <button
                onClick={() => openModal('credit')}
                className="text-purple-600 hover:text-purple-800 font-medium flex items-center"
              >
                <svg className="w-5 h-5 mr-1" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
                  <path fillRule="evenodd" d="M10 5a1 1 0 011 1v3h3a1 1 0 110 2h-3v3a1 1 0 11-2 0v-3H6a1 1 0 110-2h3V6a1 1 0 011-1z" clipRule="evenodd" />
                </svg>
                Add Credit Account
              </button>
            </div>

            {plan.credit_accounts && plan.credit_accounts.length > 0 ? (
              <ul className="divide-y divide-gray-200">
                {plan.credit_accounts.map(account => (
                  <li key={account.id} className="py-3 flex justify-between">
                    <span>{account.name}</span>
                    <span className="font-medium">${account.balance.toLocaleString()}</span>
                  </li>
                ))}
              </ul>
            ) : (
              <p className="text-gray-500 text-center py-4">No credit accounts added</p>
            )}
          </div>

          {/* Loans Card */}
          <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-200">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-semibold text-purple-700">Loans</h3>
              <button
                onClick={() => openModal('loan')}
                className="text-purple-600 hover:text-purple-800 font-medium flex items-center"
              >
                <svg className="w-5 h-5 mr-1" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
                  <path fillRule="evenodd" d="M10 5a1 1 0 011 1v3h3a1 1 0 110 2h-3v3a1 1 0 11-2 0v-3H6a1 1 0 110-2h3V6a1 1 0 011-1z" clipRule="evenodd" />
                </svg>
                Add Loan
              </button>
            </div>

            {plan.loans && plan.loans.length > 0 ? (
              <ul className="divide-y divide-gray-200">
                {plan.loans.map(loan => (
                  <li key={loan.id} className="py-3 flex justify-between">
                    <span>{loan.name}</span>
                    <span className="font-medium">${loan.balance.toLocaleString()}</span>
                  </li>
                ))}
              </ul>
            ) : (
              <p className="text-gray-500 text-center py-4">No loans added</p>
            )}
          </div>

          {/* Investment Accounts Card */}
          <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-200">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-semibold text-purple-700">Investment Accounts</h3>
              <button
                onClick={() => openModal('investment')}
                className="text-purple-600 hover:text-purple-800 font-medium flex items-center"
              >
                <svg className="w-5 h-5 mr-1" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
                  <path fillRule="evenodd" d="M10 5a1 1 0 011 1v3h3a1 1 0 110 2h-3v3a1 1 0 11-2 0v-3H6a1 1 0 110-2h3V6a1 1 0 011-1z" clipRule="evenodd" />
                </svg>
                Add Investment Account
              </button>
            </div>

            {plan.investment_accounts && plan.investment_accounts.length > 0 ? (
              <ul className="divide-y divide-gray-200">
                {plan.investment_accounts.map(account => (
                  <li key={account.id} className="py-3 flex justify-between">
                    <span>{account.name}</span>
                    <span className="font-medium">${account.balance.toLocaleString()}</span>
                  </li>
                ))}
              </ul>
            ) : (
              <p className="text-gray-500 text-center py-4">No investment accounts added</p>
            )}
          </div>
        </div>
      )}

      {/* Overview Tab Content */}
      {activeTab === 'overview' && (
        <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-200">
          <h3 className="text-xl font-semibold text-purple-700 mb-4">Plan Summary</h3>
          <p className="text-gray-600 mb-6">
            This plan runs from {formatDate(plan.start_date)} to {formatDate(plan.end_date)}
            {plan.target_amount ? ` with a target amount of $${plan.target_amount.toLocaleString()}.` : '.'}
          </p>

          <div className="bg-purple-50 p-4 rounded-lg mb-6">
            <h4 className="font-medium text-purple-700 mb-2">Linked Accounts</h4>
            <div className="grid grid-cols-2 gap-4">
              <div>
                <p className="text-sm text-gray-600">Standard Accounts:</p>
                <p className="font-medium">{plan.accounts ? plan.accounts.length : 0}</p>
              </div>
              <div>
                <p className="text-sm text-gray-600">Credit Accounts:</p>
                <p className="font-medium">{plan.credit_accounts ? plan.credit_accounts.length : 0}</p>
              </div>
              <div>
                <p className="text-sm text-gray-600">Loans:</p>
                <p className="font-medium">{plan.loans ? plan.loans.length : 0}</p>
              </div>
              <div>
                <p className="text-sm text-gray-600">Investment Accounts:</p>
                <p className="font-medium">{plan.investment_accounts ? plan.investment_accounts.length : 0}</p>
              </div>
            </div>
          </div>

          <div className="flex justify-center">
            <button 
              onClick={() => setActiveTab('accounts')}
              className="bg-purple-600 hover:bg-purple-700 text-white py-2 px-6 rounded-lg"
            >
              Manage Accounts
            </button>
          </div>
        </div>
      )}

      {/* Modal for adding accounts (placeholder) */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-xl shadow-lg p-8 max-w-md w-full">
            <div className="flex justify-between items-center mb-6">
              <h3 className="text-xl font-bold text-purple-700">
                {modalType === 'standard' && 'Add Standard Account'}
                {modalType === 'credit' && 'Add Credit Account'}
                {modalType === 'loan' && 'Add Loan'}
                {modalType === 'investment' && 'Add Investment Account'}
              </h3>
              <button onClick={closeModal} className="text-gray-500 hover:text-gray-700">
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path>
                </svg>
              </button>
            </div>

            <div className="text-center py-8 text-gray-600">
              <p className="mb-4">This feature will be implemented soon.</p>
              <p>You'll be able to add {modalType} accounts to your plan here.</p>
            </div>

            <div className="flex justify-end mt-6">
              <button
                onClick={closeModal}
                className="bg-purple-600 hover:bg-purple-700 text-white py-2 px-6 rounded-lg"
              >
                Close
